mapper.select:
SELECT 

FROM tcharge a

WHERE a.id = #{id}

SELECT 

FROM tcharge a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.prj_num = #{project.prjNum}


		AND a.report_staff = #{reportStaff.id}


		AND DATE_FORMAT(a.report_date,'%Y-%m-%d') >= DATE_FORMAT(#{dateFrom},'%Y-%m-%d')


		AND DATE_FORMAT(a.report_date,'%Y-%m-%d') <= DATE_FORMAT(#{dateTo},'%Y-%m-%d')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tcharge a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
		UPDATE tcharge SET
			approve_staff = #{approveStaff.id},
			approve_date = #{approveDate},
            approve_memo = #{approveMemo},
			status = #{status}
		WHERE id = #{id}

UPDATE tcharge SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
