mapper.select:
SELECT 

FROM tcharge a

WHERE a.id = #{id}

SELECT 

FROM tcharge a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.prj_num = #{prjNum}


		AND a.prj_name = #{prjName}


		AND a.prj_address = #{prjAddress}


		AND a.report_date = #{reportDate}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tcharge a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tcharge SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
