mapper.select:
SELECT 

FROM tchecklist a

WHERE a.id = #{id}

SELECT 

FROM tchecklist a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.companyname = #{companyname}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tchecklist a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tchecklist SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
