mapper.select:
SELECT 

FROM tdeduction_doc a

WHERE a.id = #{id}

SELECT 

FROM tdeduction_doc a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.charge_id = #{chargeId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.prj_num = #{prjNum}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tdeduction_doc a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tdeduction_doc SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
