mapper.select:
SELECT 

FROM tdeduction_doc_item a

WHERE a.id = #{id}

SELECT 

FROM tdeduction_doc_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.doc_id = #{docId}


		AND a.item_id = #{itemId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tdeduction_doc_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tdeduction_doc_item SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
