FROM tdeduction_doc_item a

WHERE a.id = #{id}

SELECT 

FROM tdeduction_doc_item a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.doc_id = #{doc.id}


		AND b.charge_id = #{doc.charge.id}


		AND a.item_id = #{item.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.id ASC

SELECT 

FROM tdeduction_doc_item a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.id ASC

mapper.update:
