mapper.select:
SELECT 

FROM topinion_book a

WHERE a.id = #{id}

SELECT 

FROM topinion_book a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.prj_num = #{prjNum}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.document_no = #{documentNo}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM topinion_book a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE topinion_book SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
