mapper.select:
SELECT 

FROM tproject a

WHERE a.id = #{id}

SELECT 

FROM tproject a

WHERE a.prj_num = #{prjNum}

SELECT
FROM tproject a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.prj_num = #{prjNum}
AND a.prj_name LIKE '%'||#{prjName}||'%' '%'+#{prjName}+'%' concat('%',#{prjName},'%')
AND a.build_corp_name LIKE '%'||#{buildCorpName}||'%' '%'+#{buildCorpName}+'%' concat('%',#{buildCorpName},'%')
AND a.build_corp_code = #{buildCorpCode}
AND a.prj_address LIKE '%'||#{prjAddress}||'%' '%'+#{prjAddress}+'%' concat('%',#{prjAddress},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tproject a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tproject SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
