SELECT 

FROM tproject_license a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.charge_id = #{charge.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM tproject_license a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE tproject_license SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE charge_id = #{charge.id}
